import pandas as pdimport numpy as np  # Import numpy for NaN handling# Load the Excel file into a DataFrame without headerexcel_file_path = 'data-hate-2-e.xlsx'df = pd.read_excel(excel_file_path, engine='openpyxl', header=None)# Filter rows with * in the second column and keep only the first columncontains_asterisk = df[df.iloc[:, 1] == '*'].iloc[:, 0]# Check for NaN values in contains_asterisk and remove themcontains_asterisk = contains_asterisk.dropna()# Filter rows without * in the second column and split cells into separate rowsdoes_not_contain_asterisk = df[df.iloc[:, 1] != '*'].iloc[:, 0]# Check for NaN values in does_not_contain_asterisk and remove themdoes_not_contain_asterisk = does_not_contain_asterisk.dropna()# Save the filtered DataFrames to separate Excel filescontains_asterisk.to_excel('data-hate-star.xlsx', index=False, header=False)does_not_contain_asterisk.to_excel('data-hate-without-star.xlsx', index=False, header=False)